mapper.select:
select t_data.recipt_id , t_data.buy_date , t_data.category_id , m_category.category_name , t_data.item_name , t_data.shop_name , t_data.spent_money , t_data.input_date , t_data.update_date , t_data.del_flg from t_data left join m_category on t_data.category_id = m_category.category_id
formatdatetime(t_data.buy_date,'yyyyMM') = #{yyyyMM}
order by t_data.recipt_id

select formatdatetime(buy_date, 'dd') as dd , sum(spent_money) as sum from t_data
formatdatetime(buy_date, 'yyyyMM') = #{yyyyMM}
group by formatdatetime(buy_date, 'yyyyMMdd') order by formatdatetime(buy_date, 'dd')

select m_category.category_name as category_name , sum(t_data.spent_money) as sum from t_data left join m_category on t_data.category_id = m_category.category_id
formatdatetime(t_data.buy_date, 'yyyyMM') = #{yyyyMM}
group by t_data.category_id order by t_data.category_id

mapper.insert:
insert into t_data (
	item_id
	, recipt_id
	, buy_date
	, category_id
	, item_name
	, shop_name
	, spent_money
	, input_date
	, update_date
	, del_flg
)
select
	#{itemId}
	, #{reciptId}
	, #{buyDate}
	, category_id
	, #{itemName}
	, #{shopName}
	, #{spentMoney}
	, #{inputDate}
	, #{updateDate}
	, #{delFlg}
from m_category

	category_name = #{categoryName}
